mapper.select:

      select news_id, news_title, news_content, news_writer, news_date,category,replycnt from(
select /*+INDEX_DESC(vam_board pk_board) */ rownum  as rn, news_id, news_title, news_content, news_writer, news_date,category,replycnt 
   from news where rownum <= #{pageNum} * #{amount} 






            order by news_id desc)   

   where rn > (#{pageNum} -1) * #{amount}

mapper.insert:
INSERT INTO news ( NEWS_ID, NEWS_TITLE, NEWS_CONTENT, CATEGORY, NEWS_WRITER
) VALUES( news_SEQ.NEXTVAL, #{news_title}, #{news_content}, #{category}, #{news_writer}
)

mapper.update:
	UPDATE BOARD
	SET
    BOARD_TITLE = #{board_title},
	BOARD_CONTENT = #{board_content}
	WHERE
	BOARD_ID = #{board_id}

update board b
set b.replycnt = (select count(reply_id) from reply where board_id = #{board_id})
where b.board_id = #{board_id}
